import logo from './logo.svg';
import './App.css';
import {useState} from "react";

const teams = [
    ["Celtics", 0.3],
    ["Thunder", 0.0],
    ["Nuggets", 0.22],
    ["Knicks", 0.0],
    ["Mavericks", 0.0], // same at home
    ["Timberwolves", 0.0],
    ["76ers", 0.08],
    ["Bucks", 0.0],
    ["Pacers", 0.0],
    ["Kings", 0.05],
    ["Grizzlies", 0.0],
    ["Pelicans", 0.0],
    ["Lakers", 0.19],
    ["Clippers", 0.0],
    ["Heat", 0.0], // worse at home
    ["Magic", 0.0],
    ["Cavs", 0.0],
    ["Suns", 0.0],
    ["Rockets", 0.31],
    ["Warriors", 0.0],
    ["Raptors", 0.06],
    ["Hawks", 0.15],
    ["Bulls", 0.0],
    ["Spurs", 0.0],
    ["Jazz", 0.0],
    ["Hornets", 0.0],
    ["Wizards", 0.0],
    ["Pistons", 0.01],
    ["Blazers", 0.0],
    ["Nets", 0.0]
]

function App() {
    const [away, setAwayTeam] = useState("")
    const [awayIp, setAwayIp] = useState("")
    const [home, setHomeTeam] = useState("")
    const [homeIp, setHomeIp] = useState("")
    const [result, setResult] = useState('');
    const [prs, setPrs] = useState("");
    const [injuries, setInjuries] = useState("");
    const getSpread = () => {
            let awayPr = 0, homePr = 0, homeBonus = 0;
            setAwayTeam(document.getElementById("awayTeam").value)
            setHomeTeam(document.getElementById("homeTeam").value)
            setAwayIp(document.getElementById("awayIp").value)
            setHomeIp(document.getElementById("homeIp").value)

            console.log('------------')
            for (let i = 0 ; i < teams.length ; i++) {
                if (teams[i][0].toString() === away.toString()  ) {
                    awayPr = (30 - i) * 65
                    console.log(away, " have a PR of ", awayPr)
                    // adjust for injuries
                    awayPr = awayPr - (awayIp * 5)
                    console.log(away, " have an IP of " , awayIp * 5)
                }
                if (teams[i][0].toString() === home.toString() ) {
                    homePr = (30 - i) * 65
                    console.log(home, " have a PR of ", homePr)
                    // adjust for injuries
                    homePr = homePr - (homeIp * 5)
                    console.log(home, " have an IP of " , homeIp * 5)
                    // adjust for home court advantage
                    homePr = homePr + (homePr * teams[i][1]);
                    console.log(home , " have a home court advantage of " , teams[i][1] * homePr)
                }
            }
            setPrs(`${away} (${awayPr}) @ ${home} (${homePr})`)
            setInjuries(`${away} receive injury penalty of ${awayIp * 5} IP `)
            console.log()
            if (homePr >= awayPr) {
                setResult(`The ${home} are favoured by  ${((homePr - awayPr) / 100).toFixed(1)} points!`)
            }
            else {
                setResult(`The ${away} are favoured by  ${((awayPr - homePr) / 100).toFixed(1)} points!`)
            }

        }
    return (
      <div className="App">
          <p>Away</p>
        <input type="text" placeholder = "away team" id = "awayTeam"/>
        <input type="number" placeholder = "away injuries (per)" id = "awayIp"/>
          <br/>
          <p>Home</p>
        <input type="text" placeholder = "home team" id = "homeTeam"/>
        <input type="number" placeholder = "home injuries (per)" id = "homeIp"/>
        <br/><br/>
          <button onClick={getSpread}>Get Spread</button>
          <p>{prs}</p>
          <p>{injuries}</p>
          <p>{result}</p>
      </div>
  );
}



export default App;
