import logo from './logo.svg';
import './App.css';
import {useState} from "react";


const teams = [
    ["Celtics", 0.3],
    ["Thunder", 0.0],
    ["Nuggets", 0.22],
    ["Knicks", 0.0],
    ["Mavericks", 0.0],
    ["Timberwolves", 0.0],
    ["76ers", 0.08],
    ["Bucks", 0.0],
    ["Pacers", 0.0],
    ["Kings", 0.05],
    ["Grizzlies", 0.0],
    ["Pelicans", 0.0],
    ["Lakers", 0.19],
    ["Clippers", 0.0],
    ["Heat", 0.0], // worse at home
    ["Magic", 0.0],
    ["Cavs", 0.0],
    ["Suns", 0.0],
    ["Rockets", 0.31],
    ["Warriors", 0.0],
    ["Raptors", 0.06],
    ["Hawks", 0.15],
    ["Bulls", 0.0],
    ["Spurs", 0.0],
    ["Jazz", 0.0],
    ["Hornets", 0.0],
    ["Wizards", 0.0],
    ["Pistons", 0.01],
    ["Blazers", 0.0],
    ["Nets", 0.0]
]


function getSpread() {

    setAwayTeam(document.getElementById("awayTeam").value)
    setHomeTeam(document.getElementById("homeTeam").value)
    setAwayIp(document.getElementById("awayIp").value)
    setHomeIp(document.getElementById("homeIp").value)


    for (let i = 0 ; i < teams.length ; i++) {
        if (teams[i][0].toString() === away.toString()  ) {
            setAwayPr((30 - i) * 65)
            setAwayPr(awayPr - (awayIp * 5) )
        }
        if (teams[i][0].toString() === home.toString() ) {
            homePr = (30 - i) * 65
            let homeApr = homePr - (homeIp * 5)
            homeBonus = homeApr * teams[i][1];
        }
    }
    console.log(away, "( ", awayPr, ") @", home , "(", homePr, ")");
    console.log
    console.log(home , " gets a home court advantage of " , homeBonus);
}

function App() {
    const [away, setAwayTeam] = useState("")
    const [awayIp, setAwayIp] = useState("")
    const [awayPr, setAwayPr] = useState(0)
    const [home, setHomeTeam] = useState("")
    const [homeIp, setHomeIp] = useState("")
    const [homePr, setHomePr] = useState("")


    return (
      <div className="App">
          <p>Away</p>
        <input type="text" placeholder = "away team" id = "awayTeam"/>
        <input type="number" placeholder = "away injuries (per)" id = "awayIp"/>
          <br/>
          <p>Home</p>
        <input type="text" placeholder = "home team" id = "homeTeam"/>
        <input type="number" placeholder = "home injuries (per)" id = "homeIp"/>
        <br/>
          <button onClick={getSpread}>Get Spread</button>
      </div>
  );
}

export default App;
